// src/CardSwiping.js
import React from 'react';
import TinderCard from 'react-tinder-card';
import Slider from 'react-slick';
import './index.css';

function NextArrow(props) {
  const { onClick } = props;
  return (
    <div className="arrow next" onClick={onClick}>
      ➔
    </div>
  );
}

function PrevArrow(props) {
  const { onClick } = props;
  return (
    <div className="arrow prev" onClick={onClick}>
      ←
    </div>
  );
}

function CardSwiping({ house, onSwipe }) {
  const sliderSettings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    nextArrow: <NextArrow />,
    prevArrow: <PrevArrow />
  };

  return (
    <TinderCard
      className="swipe"
      onSwipe={(direction) => onSwipe(direction, house.name)}
      preventSwipe={["up", "down"]}
    >
      <div className="card">
        <Slider {...sliderSettings}>
          {house.images.map((imgUrl, index) => (
            <div key={index} className="carouselImage">
              <img src={imgUrl} alt={`${house.name} - ${index + 1}`} />
            </div>
          ))}
        </Slider>
        <h3 style={{ color: 'black' }}>{house.name}</h3>
      </div>
    </TinderCard>
  );
}

export default CardSwiping;
