import React from 'react';
import './index.css';

const RecommendedHouses = ({ recommendedHouses }) => {
  return (
    <div className="cardContainer">
      {recommendedHouses.map((house, index) => (
        <div key={index} className="card">
          {/* Image Carousel */}
          <div className="carouselImage">
            <img
              src={
                house.images && house.images.length > 0
                  ? house.images[0]
                  : '/path/to/placeholder.jpg'  // Placeholder image if no images available
              }
              alt={house.name || 'House Image'}
            />
          </div>

          {/* Card content (title and description) */}
          <div className="cardContent">
            <h3>{house.name || 'House Name'}</h3>
            <div className="houseDescription">
              <p>{house.description || 'No description available.'}</p>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
};

export default RecommendedHouses;
